import { useEffect, useRef, useState } from "react"
import { useAppSelector } from "../../../store/hooks"
import mainJSON from "../mainJSON/fst.json"
import "./main_fst.css"
import gsap from "gsap"

export function MainFst(){
    
    const theme = useAppSelector(state => state.theme)
    const [title,setTitie] = useState<string>("Hello World")

    useEffect(()=>{
        const tl = gsap.timeline()
        tl.to(".main-fst-title",{
            duration:2,
        })
        mainJSON.data.map((item,idx)=>{
            tl.to(".main-fst-title",{
                duration:0.022,
                onComplete:()=>{
                    setTitie(item)
                }
            })
        })
        tl.to(".main-fst-title",{
            duration:2,
        })
        mainJSON.data2.map((item,idx)=>{
            tl.to(".main-fst-title",{
                duration:0.022,
                onComplete:()=>{
                    setTitie(item)
                }
            })
        })
        return ()=>{
            tl.kill()
        }
    },[])

    return (
        <div className="container-main-fst">
            <div className="main-fst-title">
                <span>
                    {title}
                </span>
            </div>
            <div className="main-fst-content">
                <MySVGComponent/>
                <div className="mt-5">
                    이 사이트는 mem0입니다! mem0는 텍스트 에티터를 구현과 mongoDB를 사용해보고 싶어서 만들어봤어요. <br/>
                </div>

                <NVSVGcomponent/>
            
                <div>
                    예전부터 노션이나 벨로그 같이 글쓰기 편한 에디터들은 어떻게 구현하는지 너무 궁금했는데, 이참에 많이 부족하지만 그들과 비슷하게 만들어 보았어요.
                </div>

                <div>
                    몇몇 커맨드는 부자연스럽지만, 시간되면 점차 추가해서 자연스러운 동작을 하도록 하겠습니다.
                </div>
            </div>
        </div>
    )
}

const MySVGComponent = () => {

    const svg_color = useAppSelector(state => state.theme.svg_color)
    

    useEffect(()=>{
        const tl = gsap.timeline()
        tl.to('.hand',{
            transformOrigin:"bottom left",
            rotate:"15deg",
            yoyo:true,
            repeat:4,
            ease:"power1.inOut",
            duration:0.7
        })
        tl.to('.hand',{
            duration:1.5
        })
        tl.to('.hand',{
            transformOrigin:"bottom left",
            rotate:"0deg",
            ease:"power1.inOut",
            duration:0.5
        })
        return ()=>{
            tl.kill()
        }
    },[])

  return (
    <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 354.33 354.33" width="125" height="125">
      <circle className="cls-1" cx="177.17" cy="35.85" r="25.51" fill="none" stroke={svg_color} strokeWidth="15"/>
      <line className="cls-1" x1="209.68" y1="156.65" x2="209.68" y2="354.33" fill="none" stroke={svg_color} strokeWidth="15"/>
      <path className="cls-2" d="m145.65,354.33v-143.11l-28.66-28.61.18-30.9-.18-48.52c.92-26.15,24.95-38.01,52.3-40.89,22.72-2.4,40.14,9.52,45.47,13.5l23.82,16.63" fill="none" stroke={svg_color} strokeWidth="15" strokeLinecap="round"/>
      <line className="cls-2 hand" x1="275.15" y1="33.42" x2="240.96" y2="92.92" fill="none" stroke={svg_color} strokeWidth="15" strokeLinecap="round"/>
    </svg>
  );
}

export default MySVGComponent;

const NVSVGcomponent = () => {

    const theme = useAppSelector(state => state.theme)
    
    const startTime = 0
    const scale = 90
    const iconTime = 0.7 + startTime
    const fstLineTime = 0.3 + iconTime
    const secLineTime = 0.29 + fstLineTime
    const trdLineTime = secLineTime + 0.29
    const limitRef = useRef<boolean>(false)

    const svgScale = {
        height:`${scale}px`,
        width:`${scale}px`,
    }

    const fstStyleOut = {
        height:`${scale}px`,
        width:"5rem"
    }
    const fstStyle = {
        height:`${scale}px`,
        borderTop:`1px solid ${theme.textColor}`,
        borderBottom:`1px solid ${theme.textColor}`
    }

    const secStyle = {
        height:`${scale}px`,
    }

    const secBorderStyle = {
        height:`${scale/2}px`,
        borderLeft:`1px solid ${theme.textColor}`,
    }

    const trdStyle = {
        borderBottom:`1px solid ${theme.textColor}`,
        height:"0px",
        width:`2rem`,
    }


    useEffect(()=>{
        const tl = gsap.timeline()
        if(!limitRef.current){
            const tl = gsap.timeline()
            tl.set(".container-NVSVG",{
                height:0,
            })
            tl.to(".container-NVSVG",{
                duration:startTime
            })
            tl.to(".container-NVSVG",{
                duration:1,
                height:"auto"
            })
            tl.fromTo(".notion-svg",{
                opacity:0,
                x:-45
            },{
                opacity:1,
                x:0,
                duration:0.5
            },iconTime - 0.2)
            tl.fromTo(".velog-svg",{
                opacity:0,
                x:-45
            },{
                opacity:1,
                x:0,
                duration:0.5
            },iconTime)
            tl.fromTo(".fstline",{
                scaleX:0,
                borderTop:`0px solid ${theme.textColor}`,
                borderBottom:`0px solid ${theme.textColor}`,
            },{
                borderTop:`1px solid ${theme.textColor}`,
                borderBottom:`1px solid ${theme.textColor}`,
                transformOrigin: 'left',
                scaleX:1,
                duration:0.3
            },fstLineTime)
            tl.fromTo(".sec_1_line",{
                scaleY:0,
                borderLeft:`0px solid ${theme.textColor}`,
                transformOrigin:"top"
            },{
                duration:0.3,
                scaleY:1,
                borderLeft:`1px solid ${theme.textColor}`,
            },secLineTime)
            tl.fromTo(".sec_2_line",{
                scaleY:0,
                borderLeft:`0px solid ${theme.textColor}`,
                transformOrigin:"bottom"
            },{
                scaleY:1,
                borderLeft:`1px solid ${theme.textColor}`,
                duration:0.3
            },secLineTime)
            tl.fromTo(".trdline",{
                scaleX:0,
                borderLeft:`0px solid ${theme.textColor}`,
                transformOrigin:"left",
                duration:0.3
            },{
                scaleX:1,
                borderLeft:`1px solid ${theme.textColor}`,
                duration:0.5
            },trdLineTime)

            tl.fromTo(".fourthline",{
                x:-15,
                opacity:0,
            },{
                x:-15,
                opacity:1,
                duration:0.5
            },trdLineTime)

        }
        
    },[theme])


    return (
        <div className="container-NVSVG flex flex-row items-center justify-center">
            <div className="flex flex-col">
                <div style={svgScale} className="flex items-center justify-center notion-svg">
                    <NotionSVG/>
                </div>
                <div style={svgScale} className="flex items-center justify-center velog-svg">
                    <VelogSVG/>
                </div>
            </div>
                <div style={fstStyleOut}>
                    <div className="fstline" style={fstStyle}/>
                </div>
                <div style={fstStyle}>
                    <div style={secStyle}>
                        <div style={secBorderStyle} className="sec_1_line"/>
                        <div style={secBorderStyle} className="sec_2_line"/>                        
                    </div>
                </div> 
                <div style={trdStyle} className="trdline"/>
                <div className="fourthline" style={{marginLeft:"16px"}}>
                    <EditSVG/>
                </div>
                

        </div>
    )
}

